import React, { useContext, useEffect } from 'react';
import { ProductOnline } from "../class/ProductOnline";
import { UserOnline } from '../class/UserOnline';
import { Link} from 'react-router-dom';
import '../css/HomePage.css'; 
function ProductsView() {
  const { user, setUser } = useContext(UserOnline);
  const { products, setProducts } = useContext(ProductOnline);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('/api/products/all', {
          method: 'GET'
        });

        const data = await response.json();
        const productList = data.List;

        if (Array.isArray(productList)) {
          setProducts(productList);
          console.log(productList);
        } else {
          console.error("La respuesta no contiene una lista válida");
        }
      } catch (err) {
        alert("Error de conexión con el servidor. Ha fallado la obtención de productos.");
      }
    };

    fetchProducts();
  }, [setProducts]);

  return (
      <div class="container">

        <aside class="sidebar">
          <nav>
            <ul>
              <li>
              <Link to={`/home/${user?.firstName}/allUsers` || "Nada"}>Usuarios</Link>
              </li>
              <li>
              <Link to={`/home/${user?.firstName}/products` || "Nada"}>Productos</Link>
              </li>
              <li>
              <Link to={`/home/${user?.firstName}` || "Nada"}>Perfil</Link>
              </li>
              <li>Registros</li>
            </ul>
          </nav>
        </aside>

        <main class="content">
          <header>
            <p class="date"> {Date.now } </p>
            <h1>Bienvenido {user?.firstName || "invitado"}</h1>
            <button onClick={() => { setUser(null); }}>
            Cerrar sesión
            </button>
          </header>

          <section class="applications">
            <h3>Lorem ipsum</h3>
            <div class="card">
                {products?.map(p => (

                  


                  <div key={p.isbn}>
                    <div className='imageBox'>
                      <img src={`data:image/jpeg;base64,${p.images}`} alt="Imagen del producto" />




                    </div>
                    <h3>{p.productName}</h3>
                    <p>{p.information}</p>
                  </div>
                ))}
            </div>
          </section>
        </main>
      </div>
  );
}

export default ProductsView;
