import React, { useContext, useEffect } from 'react';
import { UserListOnline } from "../class/UserListOnline";
import { UserOnline } from '../class/UserOnline';
import { Link} from 'react-router-dom';
import '../css/HomePage.css'; 

function UserView() {
  const { user, setUser } = useContext(UserOnline);
  const { list, setListUser } = useContext(UserListOnline);

  useEffect(() => {
    const fetchlist= async () => {
      try {
            const rol = user?.Rol;

            if (rol && ["Administrador", "Usuario", "Invitado"].includes(rol)) {
            const response = await fetch(`/api/user/allUsers?rol=${rol}`, {
                method: 'GET',
            });
            } else {
            console.error("Rol inválido o no definido");
            }



        const data = await response.json();
        const userList = data.List;

        if (Array.isArray(userList)) {
          setListUser(userList);
          console.log(userList);
        } else {
          console.error("La respuesta no contiene una lista válida");
        }
      } catch (err) {
        alert("Error de conexión con el servidor. Ha fallado la obtención de usuarios.");
      }
    };

    fetchlist();
  }, [setListUser]);

  return (
      <div class="container">

        <aside class="sidebar">
           <nav>
            <ul>
              <li>
              <Link to={`/home/${user?.firstName}/allUsers` || "Nada"}>Usuarios</Link>
              </li>
              <li>
              <Link to={`/home/${user?.firstName}/products` || "Nada"}>Productos</Link>
              </li>
              <li>
              <Link to={`/home/${user?.firstName}` || "Nada"}>Perfil</Link>
              </li>
              <li>Registros</li>
            </ul>
          </nav>
        </aside>

        <main class="content">
          <header>
            <p class="date"> {Date.now } </p>
            <h1>Bienvenido {user?.firstName || "invitado"}</h1>
            <button onClick={() => { setUser(null); }}>
            Cerrar sesión
            </button>
          </header>

          <section class="applications">
        
            <div class="card">
                {list?.map(u => (

                  <div key={u.idUser}>
                    <h3>{u.firstName}</h3>
                    <p>{u.companyMail}</p>
                  </div>
                ))}
            </div>
          </section>
        </main>
      </div>
  );
}

export default UserView;
