import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserOnline } from './UserOnline';


function Form() {
  const [mail, setMail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

const { user } = useContext(UserOnline); // Setter del contexto

  

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`/api/verifierUser?mail=${encodeURIComponent(mail)}&password=${encodeURIComponent(password)}`);
      const data = await response.json();

      if (response.ok && data.userOnline) {
        setUser(data); // Guardo objeto
        navigate(`/home/${data.userOnline.firstName}`); // Redireccionamos a la página.
      } else {
        alert(data.error || "Credenciales inválidas");
      }
    } catch (err) {
      alert("Error de conexión con el servidor.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="mail">Mail</label>
      <input type="email" id="mail" value={mail} onChange={(e) => setMail(e.target.value)} required />

      <label htmlFor="password">Password</label>
      <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />

      <button type="submit">Subir</button>
    </form>
  );
}

export default Form;



