import React, { useContext, useEffect } from 'react';
import { UserOnline } from './UserOnline';
import { useNotification } from './NotificationContext';
import { useNavigate } from 'react-router-dom';
function HomePage() {
  const { notify } = useNotification(); 
  const { user, setUser } = useContext(UserOnline);
  const navigate = useNavigate(); 
  
  //Para comprobar cambios constantes es necesario envolver el condicional en useEffect.
  //o cosas similares como un handleSubmit, handleLogin, ..etc.
 useEffect(() => {
  if (!user) {
    notify({
      type: "error",
      message: "No hay ningún usuario presente",
      position: "top-center"
    });
    navigate("/");
  }
}, [user, notify, navigate]); 



    
  return (
    <>




      <div> 
        <h1>Bienvenido {user.firstName}</h1>
      
        <button onClick={() => { setUser(null); }}>
            Cerrar sesión
        </button>

        


      
      </div>
      <header>
        <ul>
          <li>Usuarios</li>
          <li>Productos</li>
          <li>Extra</li>
          <li>Extra</li>
          <li>Extra</li>
        </ul>

      </header>
       
   
      <footer>


      </footer>
      
    </>
  );
}

export default HomePage;
