import React, { useContext } from 'react';
import { ProductOnline } from "./ProductOnline";

function ProductsView() {
  const { products, setProducts } = useContext(ProductOnline);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('api/products/all', {
        method: 'GET'
      });

      const data = await response.json();
      const productList = data.List;

      if (Array.isArray(productList)) {
        setProducts(productList); // ✅ guarda toda la lista
        console.log(productList);
      } else {
        console.error("La respuesta no contiene una lista válida");
      }

    } catch (err) {
      alert("Error de conexión con el servidor. Ha fallado la obtención de productos.");
    }
  };

  return (
    <div className='firstBox'>
      <header>
        <h1>BLA BLA BLA</h1>
        <button onClick={handleSubmit}>Cargar productos</button>
      </header>

      <div>
        {products?.map(p => (
          <div key={p.isbn}>
            <h3>{p.productName}</h3>
            <p>{p.information}</p>
          </div>
        ))}
      </div>

      <footer>
        <h1>BLA BLA BLA</h1>
      </footer>
    </div>
  );
}

export default ProductsView;
